import './App.css';
import * as React from 'react';
import Paper from '@mui/material/Paper';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import { styled } from '@mui/material/styles';
import MonacoEditor from 'react-monaco-editor';
import { Flipped, Flipper } from "react-flip-toolkit";
import Grid from '@mui/material/Grid';

const Item = styled(Paper)(({ theme }) => ({
  ...theme.typography.body2,
  padding: theme.spacing(2),
  fontSize: '75%',
  textAlign: 'center',
  alignContent: 'center',
  color: theme.palette.text.primary,
}));


export default class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      array: Array.from({length: 12}, () => Math.floor(Math.random() * 100)),
      logIndex: 0,
      logs: [],
      runtime: 0,
      highlighted: [],
      buttonLock: true,
    }

    this.onLogsReturned = this.onLogsReturned.bind(this)
  }

  onLogsReturned(logs) {
    this.setState({ logs: logs, logIndex: 0, buttonLock: false })
  }

  stepForward() {
    if (this.state.buttonLock) return
    const logIndexNext = this.state.logIndex + 1
    const logs = this.state.logs
    if (logIndexNext >= logs.length) {
      return
    }
    const currentLog = logs[logIndexNext]
    this.setState({ logIndex: logIndexNext, array: currentLog["array"], highlighted: currentLog["indices"] })
  }

  stepBack() {
    if (this.state.buttonLock) return
    const logIndex = this.state.logIndex
    const logs = this.state.logs
    if (logIndex <= 0) {
      return
    }
    const prevLog = logs[logIndex - 1]
    const currentLog = logs[logIndex]
    this.setState({ logIndex: logIndex - 1, array: prevLog["array"], highlighted: currentLog["indices"] })
  }

  renderCellWithColor(num, index) {
    if (this.state.highlighted.includes(index)) {
      return <Item style={{ backgroundColor: '#BBB15AFF' }}>{num}</Item>
    } else {
      return <Item>{num}</Item>
    }
  }

  async onAnimationComplete(flipped) {
    // Lock buttons and sleep
    this.setState({ buttonLock: true })
    await new Promise(r => setTimeout(r, 200));
    this.setState({ highlighted: [], buttonLock: false })
  }

  render() {
    return (
      <div className="App">
        <Grid container>
          <Grid item xs={7}>
            <header className="App-header">
              <h1 className="HeaderTitle">Sorting Visualizer</h1>
              <Stack className="Stack" direction="row" spacing={2}>
                <Flipper flipKey={this.state.array.join('')} onComplete={() => this.onAnimationComplete()}>
                  <ul className="list">
                    {this.state.array.map((num, index) => (
                      <Flipped key={index} flipId={num}>
                        {this.renderCellWithColor(num, index)}
                      </Flipped>
                    ))}
                  </ul>
                </Flipper>
              </Stack>
              <Stack className="Stack" direction="row" spacing={2}>
                <Button variant="contained" color="primary" onClick={() => this.stepBack()}>
                  Previous
                </Button>
                <Button variant="contained" color="primary" onClick={() => this.stepForward()}>
                  Next
                </Button>
              </Stack>
            </header>
          </Grid>
          <CodeEditor onLogsReturned={this.onLogsReturned} array={this.state.array} />
        </Grid>
      </div>
    );
  }
}

class CodeEditor extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      code: "def sorting_algorithm(arr):\n\tprint('Hello world')\n",
      output: []
    }

    this.setState({ output: [...this.state.output, this.createOutputTextComponent("Please enter your code and press submit")] })
  }

  submitCode() {
    fetch('/user-code', {
      method: 'POST',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        code: window.btoa(this.state.code),
        array: this.props.array
      })
    }).then(res => {
      var data = res.json()
      this.constructOutput(data, !res.ok)
      this.props.onLogsReturned(data.arrayLog)
    });
  }

  async constructOutput(data) {
    this.setState({ output: [...this.state.output, this.createOutputTextComponent("Code run successfully!")] })
    await new Promise(r => setTimeout(r, 1000));
    this.setState({ output: [...this.state.output, this.createOutputTextComponent("Run time: " + data.runtime)] })
  }

  async constructError(data) {
    this.setState({ output: [...this.state.output, this.createOutputTextComponent("Code failed to run with the following error:")] })
    await new Promise(r => setTimeout(r, 1000));
    this.setState({ output: [...this.state.output, this.createOutputTextComponent(data.errorMessage)] })
  }

  createOutputTextComponent(text) {
    return <p className="Output"><i>{text}</i></p>
  }

  render() {
    return (
      <Grid item xs={5}>
        <MonacoEditor
          height="70%"
          language="python"
          theme="vs-light"
          value={this.state.code}
          options={{
            selectOnLineNumbers: false,
            automaticLayout: true,
            fontSize: 15,
            minimap: {
              enabled: false
            },
          }}
          onChange={(newValue, e) => this.setState({ code: newValue })}
          editorDidMount={(editor, monaco) => editor.focus()}
        />
        <Stack className="Stack" direction="column" spacing={2}>
          <Button style={{ maxWidth: '60px', paddingLeft: 40, paddingRight: 40 }} variant="contained" color="primary" onClick={() => this.submitCode()}>
            Submit
          </Button>
          {this.state.output}
        </Stack>
      </Grid>
    );
  }
}
